// EditEntry.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const EditEntry = ({ token }) => {
  const { team_name } = useParams();
  const history = useHistory();
  const [entry, setEntry] = useState({
    team_name: '',
    total_score: '',
    callaway_score: ''
  });

  useEffect(() => {
    axios.get(`http://localhost:3000/leaderboard/${team_name}`)
      .then(response => setEntry(response.data))
      .catch(error => console.error('Error fetching entry:', error));
  }, [team_name]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEntry(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.put(`http://localhost:3000/leaderboard/${team_name}`, entry, {
      headers: { Authorization: token }
    })
    .then(() => history.push('/admin/leaderboard'))
    .catch(error => console.error('Error updating entry:', error));
  };

  return (
    <div>
      <h1>Edit Entry for {team_name}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Team Name:
          <input
            type="text"
            name="team_name"
            value={entry.team_name}
            onChange={handleChange}
            readOnly
          />
        </label>
        <label>
          Total Score:
          <input
            type="number"
            name="total_score"
            value={entry.total_score}
            onChange={handleChange}
          />
        </label>
        <label>
          Callaway Score:
          <input
            type="number"
            name="callaway_score"
            value={entry.callaway_score}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default EditEntry;
