import React, { useState, useEffect } from "react";
import axios from "axios";
import cally from "../callaway";  // adjust the path as necessary

const ScorecardLeaderboard = () => {
    const [myScorecard, setMyScorecard] = useState([]);
    const [error, setError] = useState(null);

    const fetchScorecard = async () => {
        try {
            const response = await axios.get('http://localhost:3000/scorecard');
            setMyScorecard(response.data.map(entry => {
                const list = Object.keys(entry)
                    .filter(key => key.startsWith('hole'))
                    .map(key => entry[key]);
                return new cally(list, entry.name);
            }));
        } catch (error) {
            console.error("There was an error getting the scorecard:", error);
            setError('There was an error fetching the scorecard. Please try again.');
        }
    };

    useEffect(() => {
        fetchScorecard();
    }, []);

    return (
        <div>
            <h1>Scorecards</h1>
            {error && <p>{error}</p>}
            <ul className="divide-y divide-gray-200 rounded-lg shadow-lg overflow-hidden">
                {myScorecard.map((cally, index) => (
                    <li key={cally.getName()}>
                        <div className="flex items-center">
                            <span className="font-semibold mr-4">{index + 1}</span>
                            <span className="mr-8">{cally.getName()}</span>
                            <span className="mr-8">{cally.getScorecard().join(', ')}</span>
                            <span className="mr-8">Total Score: {cally.totalScore()}</span>
                            <span className="mr-8">Calculated Score: {cally.calc()}</span>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ScorecardLeaderboard;
