// AdminDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = ({ token }) => {
    const [leaderboard, setLeaderboard] = useState([]);
    const [error, setError] = useState(null);

    const fetchLeaderboard = async () => {
        try {
            const response = await axios.get('http://localhost:3000/leaderboard');
            setLeaderboard(response.data);
        } catch (error) {
            console.error('There was an error fetching the leaderboard', error);
            setError('There was an error fetching the leaderboard. Please try again.');
        }
    };

    const handleDelete = async (team_name) => {
        try {
            console.log("trying to delete with team_name:", team_name); // Check the value of id
            await axios.delete(`http://localhost:3000/leaderboard/${team_name}`, {
                headers: { Authorization: token }
            });
            fetchLeaderboard();
        } catch (error) {
            console.error('Error deleting entry', error);
            setError('Error deleting entry');
        }
    };


    // const handleEdit = (id) => {
    //     // Implement edit functionality here
    // };

    useEffect(() => {
        fetchLeaderboard();
    }, []);

    return (
        <div className="text-center my-4">
            <h2 className="text-2xl font-semibold mb-4">Admin Dashboard</h2>
            {error && <div className="text-red-500 mt-2">{error}</div>}
            <ul className="divide-y divide-gray-200 rounded-lg shadow-lg overflow-hidden">
                {leaderboard.map((entry, index) => (
                    <li key={index} className="py-4 px-6 sm:px-10 flex items-center justify-between bg-white hover:bg-gray-100 transition-colors duration-300 ease-in-out">
                        <div className="flex items-center">
                            <span className="font-semibold mr-4">{index + 1}</span>
                            <span className="mr-8">{entry.team_name}</span>
                            <span className="text-gray-600">{`Total Score: ${entry.total_score}, Callaway Score: ${entry.callaway_score}`}</span>
                        </div>
                        <div className="flex space-x-4">
                            {/* <button onClick={() => handleEdit(entry.id)} className="text-blue-500 hover:text-blue-700">Edit</button> */}
                            <button onClick={() => handleDelete(entry.team_name)} className="text-red-500 hover:text-red-700">Delete</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
