import React, { useState } from "react";
import ScoreInput from "./components/ScoreInput";
import NameInput from "./components/NameInput";

const App = () => {
  const [name, setName] =useState("");
  const [scores, setScores] = useState(Array(18).fill(0));
  const [currentHole, setCurrentHole] = useState(1);
  const [finalScore, setFinalScore] = useState(null);
  const [error, setError] = useState(null);

  const handleNameSubmit = (enteredName) => {
    setName(enteredName);
  };

  const handleScoreChange = (hole, score) => {
    const newScores = [...scores];
    newScores[hole - 1] = parseInt(score, 10);
    setScores(newScores);
  };

  const handleSubmit = async () => {
    if (currentHole < 18) {
      setCurrentHole(currentHole + 1);
    } else {
      try {
        const response = await fetch('http://localhost:3000/calculate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ scores })
        });

        if (!response.ok) {
          throw new Error('Network response was not okay');
        }

        const result = await response.json();
        console.log(result); // Debugging line to check the API response
        setFinalScore(result.callawayScore); // Ensure this line is correct
      } catch (error) {
        console.error('Failed to fetch the final score', error);
        setError('Failed to fetch the final score. Please try again.');
      }
    }
  };

  return (
    <div className="pt-10 items-center justify-center h-screen">
      <h1 className="text-5xl font-semibold mb-2 text-center">Callaway Score Input</h1>
      {name === "" ? (
        <NameInput onNameSubmit={handleNameSubmit} />
      ) : finalScore === null ? (
        <ScoreInput
          hole={currentHole}
          onScoreChange={handleScoreChange}
          onNext={handleSubmit}
        />
      ) : (
        <div className="text-center mt-4">
          <h2 className="text-2xl font-semibold">Final Score: {finalScore}</h2>
          <h3 className="text-xl">Player: {name}</h3>
          <button 
            onClick={() => {
              setScores(Array(18).fill(0));
              setCurrentHole(1);
              setFinalScore(null);
              setName("");
              setError(null);
            }}
            className="mt-4 bg-blue-700 hover:bg-blue-900 text-white font-semibold px-4 py-2 rounded"
          >
            Start New Round
          </button>
        </div>
      )}
      {error && <div className="text-red-500 mt-4">{error}</div>}
    </div>
  );
};

export default App;
